mapper.update:
UPDATE user_sessions
last_heartbeat = #{lastHeartbeat}, client_info = #{clientInfo}, ip_address = #{ipAddress}, status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE user_sessions 
SET last_heartbeat = #{heartbeatTime}, update_time = NOW()
WHERE session_id = #{sessionId}

UPDATE user_sessions 
SET status = 0, update_time = NOW()
WHERE session_id = #{sessionId}

UPDATE user_sessions 
SET status = 0, update_time = NOW()
WHERE user_id = #{userId}
